import React from 'react'

const Search = ({ search, onSearch }) => (
    
    <div className="search-container"  class="input-group">
      <label htmlFor="search" className="search-label">
        Search:
      </label>
      <input
        value={search}
        id="search"
        type="text"
        className="search-input"
        onChange={onSearch}
      />
    </div>
  );
  

    export default Search;
