import List from './components/List';
import './App.css';
import { useState } from 'react';

const list = [
  {
    title : 'react',
    url:'https://reactjs.org/',
    author :'Jordan Walke',
    num_comments : 3,
    points : 4,
    objectID : 0
  },
  {
    title : 'Redux',
    url:'https://redux.js.org/',
    author :'Dan Abramov, Andrew Clark',
    num_comments : 2,
    points : 5,
    objectID : 1
  }
  ];
//const title ='fares';
const welcome = {
greeting : "Hey",
title :"react"
};


function getTitle (title){
  return title;
}
function App() {



  return (
    <div className="App">
      <h1>{welcome.greeting} {welcome.title}</h1>
      <h1>{getTitle("test title")}</h1>
      <Search onSearch={handleChange}/>
      <List list={list}/>


    </div>
  );
}

const Search =(props)=>{
  const [searchTerm,SetSearchTerm]=useState('');
  const handleChange = (event) =>{ SetSearchTerm (event.target.value); props.onSearch(event)}
  return(
<div>
    <label htmlFor='search'> Search :</label>
    <input id="search" type="text" onChange={props.onSearch}/>
    <p> searching for {searchTerm}</p>
</div>)
}







export default App;
