import List from './components/List';
import './App.css';
import { useState } from 'react';
import Search from './components/Search';
import React from 'react'
const list = [
  {
    title : 'react',
    url:'https://reactjs.org/',
    author :'Jordan Walke',
    num_comments : 3,
    points : 4,
    objectID : 0
  },
  {
    title : 'Redux',
    url:'https://redux.js.org/',
    author :'Dan Abramov, Andrew Clark',
    num_comments : 2,
    points : 5,
    objectID : 1
  }
  ];

   function getList() {
  return fetch('https://goweather.herokuapp.com/weather/paris')
    .then(data => data.json())
}
//const title ='fares';
const welcome = {
greeting : "Hey",
title :"react"
};


function getTitle (title){
  return title;
}
function App() {
  const [searchTerm,SetSearchTerm]=useState(localStorage.getItem('recherche') || 'react');
  const [Weatherlist, SetWeatherlist] = useState([]);
   useEffect(() => {
   let mounted = true;
   getList()
     .then(items => {
       if(mounted) {
         setList(items)
       }
     })
   return () => mounted = false;
 }, [])
  React.useEffect(()=>{
    localStorage.setItem('recherche',searchTerm);
  },[searchTerm])
  const handleSearch = (event)=>{

    SetSearchTerm (event.target.value);

  }
  const searchedStories = list.filter((item)=>{
    return item.title.toLowerCase().includes(searchTerm.toLowerCase());
  })
  return (
    <div className="App">
      <h1>{welcome.greeting} {welcome.title}</h1>
      <h1>{getTitle("test title")}</h1>
      <Search search={searchTerm} onSearch={handleSearch}/>
      <p> searching for {searchTerm}</p>
      <List list={searchedStories}/>
      <ul>
        {Weatherlist.map((item)=>{
          <li>
            <span>{item.temperature}</span>
            <span>{item.wind}</span>
            <span>{item.description}</span>
          </li>
        })}
      </ul>
      <button onClick={()=>{localStorage.removeItem('recherche')}}>supprimer local storage</button>


    </div>
  );
}










export default App;
