 const fetchData = async () => {
  try {
    const response = await fetch('https://api.coincap.io/v2/assets');
    const jsonData = await response.json();
    console.log(jsonData);
    SetWeatherlist([jsonData]);
  } catch (error) {
    console.error('Error:', error);
  }
};
React.useEffect(() => {
  fetchData();
}, []);

